import React, { useEffect, useRef } from 'react';
import Highcharts from 'highcharts';
import HighchartsReact from 'highcharts-react-official';

function ChartComponent({ chartData, chartType }) {
  const chartRef = useRef(null);

  const getChartOptions = () => ({
    chart: {
      type: chartType, // Use chartType directly
    },
    title: {
      text: chartData.title, // Title from chartData
    },
    xAxis: chartType !== 'pie' ? { // Only for non-pie charts
      categories: chartData.labels,
    } : undefined,
    yAxis: chartType !== 'pie' ? { // Only for non-pie charts
      title: {
        text: 'Crime Count',
      },
    } : undefined,
    series: [{
      name: chartType === 'pie' ? 'Crime Type' : 'Crime Count', // Series name changes based on chartType
      data: chartType === 'pie'
        ? chartData.labels.map((label, index) => ({
            name: label,
            y: chartData.datasets[0].data[index],
          }))
        : chartData.datasets[0].data, // For bar/line, use raw data
    }],
    plotOptions: {
      series: {
        animation: true, // Enable smooth animation for data updates
      },
    },
  });

  // Effect to handle updates to chart type
  useEffect(() => {
    if (chartRef.current) {
      const chart = chartRef.current.chart;

      // Update the chart type dynamically
      chart.update({
        chart: {
          type: chartType,
        },
      }, true, true); // Redraw and apply animation
    }
  }, [chartType]);

  // Effect to handle updates to data
  useEffect(() => {
    if (chartRef.current) {
      const chart = chartRef.current.chart;

      // Update the series data
      chart.series[0].setData(
        chartType === 'pie'
          ? chartData.labels.map((label, index) => ({
              name: label,
              y: chartData.datasets[0].data[index],
            }))
          : chartData.datasets[0].data,
        true, // Redraw chart
        { duration: 500, easing: 'easeOutQuad' } // Animation settings
      );
    }
  }, [chartData, chartType]);

  return (
    <div style={{
      width: '80%',
      marginInline: 'auto',
      borderRadius: '15px',
      overflow: 'hidden',
      border: 'solid 2px #272626',
      padding: '1rem',
    }}>
      <HighchartsReact
        highcharts={Highcharts}
        options={getChartOptions()} // Initial options for the chart
        ref={chartRef} // Reference to the chart
      />
    </div>
  );
}

export default ChartComponent;
