import React, { useEffect, useState } from 'react';
import Highcharts from 'highcharts';
import HighchartsReact from 'highcharts-react-official';

function ChartComponent({ chartData, chartType }) {
  const [chartOptions, setChartOptions] = useState(null);
  const [loading, setLoading] = useState(true); // Track chart loading state

  useEffect(() => {
    // Delay to ensure smooth transitions
    setLoading(true);
    const timeout = setTimeout(() => {
      setChartOptions({
        chart: {
          type: chartType, // Use chartType directly
          animation: {
            duration: 800, // Animate data points
          },
        },
        title: {
          text: chartData.title, // Title from chartData
        },
        xAxis: chartType !== 'pie' ? {
          categories: chartData.labels,
        } : undefined,
        yAxis: chartType !== 'pie' ? {
          title: {
            text: 'Crime Count',
          },
        } : undefined,
        series: [{
          name: chartType === 'pie' ? 'Crime Type' : 'Crime Count',
          data: chartType === 'pie'
            ? chartData.labels.map((label, index) => ({
                name: label,
                y: chartData.datasets[0].data[index],
              }))
            : chartData.datasets[0].data,
        }],
      });
      setLoading(false); // Chart is ready to render
    }, 0); // Delay rendering

    return () => clearTimeout(timeout); // Clean up timeout
  }, [chartType, chartData]);

  return (
    <div style={{
      width: '80%',
      marginInline: 'auto',
      borderRadius: '15px',
      overflow: 'hidden',
      border: 'solid 2px #272626', 
      padding: '1rem',
    }}>
      {!loading && chartOptions && ( // Render chart only after loading is false
        <HighchartsReact
          highcharts={Highcharts}
          options={chartOptions}
        />
      )}
    </div>
  );
}

export default ChartComponent;
