import React from 'react';
import '../styles/FilterOptions.css';


function ChartTypeSelector({ chartType, setChartType }) {
  const handleChangeWithDelay = (value) => {
    setTimeout(() => {
      setChartType(value); // Set the chart type after 50 ms
    }, 1000);
  };

  return (
    <div className="chart-type-selector" >
      <strong><span>Chart Type:</span></strong>
      <label>
        <input
          type="radio"
          name="chartType"
          value="pie"
          checked={chartType === 'pie'}
          onChange={(e) => handleChangeWithDelay(e.target.value)}
        />
        <span>Pie Chart</span>

      </label>
      <label>
        <input
          type="radio"
          name="chartType"
          value="bar"
          checked={chartType === 'bar'}
          onChange={(e) => handleChangeWithDelay(e.target.value)}
        />
        <span>Bar Chart</span>
      </label>
      <label>
        <input
          type="radio"
          name="chartType"
          value="line"
          checked={chartType === 'line'}
          onChange={(e) => handleChangeWithDelay(e.target.value)}
        />
        <span>Line</span>
      </label>
    </div>

  );
}

export default ChartTypeSelector;
