import React from 'react';
import Highcharts from 'highcharts';
import HighchartsReact from 'highcharts-react-official';

function ChartComponent({ chartData, chartType }) {
  const getChartOptions = (type) => {
    switch (type) {
      case 'pie':
        return {
          chart: {
            type: 'pie',
          },
          title: {
            text: chartData.title, // Use title from chartData
          },
          series: [{
            name: 'Crime Type',
            data: chartData.labels.map((label, index) => ({
              name: label,
              y: chartData.datasets[0].data[index],
            })),
          }],
        };
      case 'line':
        return {
          chart: {
            type: 'line',
          },
          title: {
            text: chartData.title, // Use title from chartData
          },
          xAxis: {
            categories: chartData.labels,
          },
          yAxis: {
            title: {
              text: 'Crime Count',
            },
          },
          series: [{
            name: 'Crime Count',
            data: chartData.datasets[0].data,
          }],
        };
      case 'bar':
        return {
          chart: {
            type: 'bar',
          },
          title: {
            text: chartData.title, // Use title from chartData
          },
          xAxis: {
            categories: chartData.labels,
          },
          yAxis: {
            title: {
              text: 'Crime Count',
            },
          },
          series: [{
            name: 'Crime Count',
            data: chartData.datasets[0].data,
          }],
        };
      default:
        return {};
    }
  };

  return (
    <div style={{
        width: '80%',
        marginInline: 'auto',
        borderRadius: '15px',
        overflow: 'hidden',
        border: 'solid 2px #272626', 
        padding: '1rem',
    }}>
      <HighchartsReact
        highcharts={Highcharts}
        options={getChartOptions(chartType)} // Pass the options dynamically based on the selected chart type
      />
    </div>
  );
}

export default ChartComponent;
